/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* removeZeroSumSublists(ListNode* head) {
        if (head == NULL) return head;

        ListNode* myHead = new ListNode(0, head);
        stack<int>sums;
        unordered_map<int,ListNode*>dict; //Key = Sum, Val = ListNode
        int sum = 0;

        sums.push(0);
        dict[0] = myHead;

        while(head != NULL) {

            sum += head->val;

            if (dict.find(sum) != dict.end()) {

                head = head->next;

                while(sums.top() != sum) {

                    dict.erase(sums.top());
                    sums.pop();

                }//while stack

                dict[sums.top()]->next = head;
            }//if val
            else {

                sums.push(sum);
                dict[sum] = head;
                head = head->next;

            }//else val

        }//while

        head = myHead->next;

        myHead->next = NULL;
        delete myHead;

        return head;
    }
};
/*
KK

Time Complexity: O(N)
Space Complexity: O(N)
*/
