/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {

    public void cleanMap(ListNode node, int sum, ListNode head, HashMap<Integer, ListNode> map ) {
        if(node == head) return;
        sum+=node.val;
        map.remove(sum);
        cleanMap(node.next, sum, head, map);
    }

    public ListNode removeZeroSumSublists(ListNode head) {
        HashMap<Integer, ListNode> map = new HashMap<>();
        ListNode root = new ListNode(0);
        root.next = head;
        map.put(0, root);
        int sum = 0;
        while(head!=null){
            sum+=head.val;
            if(map.containsKey(sum)){
                ListNode node = map.get(sum);
                cleanMap(node.next, sum, head, map);
                node.next = head.next;
            }
            else{
                map.put(sum, head);
            }
            head = head.next;
        }
        return root.next;
    }
}
