    var set = Set[Int]()
    set add 0
    var result = new ListNode(-1)
    result.next = head

    var stack = Stack[ListNode]()
    var runner = head
    var sum = 0

    while (runner != null) {

      val currVal = runner.x
      val curr = runner
      sum += currVal

      if (set.add(sum)) {
        stack push curr
      } else {

        val next = curr.next
        var tmpSum = sum
        sum -= currVal

        while (!stack.isEmpty && tmpSum != sum) {
          set remove sum
          sum -= (stack pop()).x
        }

        if (!stack.isEmpty) {
          stack.top.next = next
        } else {
          result.next = next
        }
      }
      runner = runner.next
    }
    result.next
  }
```

