    public ListNode removeZeroSumSublists(ListNode head) {
        ListNode dummy = new ListNode(-1);
        dummy.next = head;
        int sum = 0;
        Map<Integer, ListNode> map = new HashMap<>();
        map.put(0, dummy);
        while (head != null) {
            sum += head.val;
            map.putIfAbsent(sum, head);
            if (map.containsKey(sum)) {
                map.get(sum).next = head.next;
            }
            head = head.next;
        }
        return dummy.next;
    }
}
