        if (n == 0) return 1; // Found a valid answer
        if (i == 5) return 0; // Reach to length of vowels [a, e, i, o, u]
        if (memo[n][i] != null) return memo[n][i];

        int ans = dp(n, i+1, memo); // Skip vowels[i]
        ans += dp(n-1, i, memo); // Include vowels[i]

        return memo[n][i] = ans;
    }
}
