        // for init, dp[1][*] = 1, dp[1][0] = 1
        // we can transfer the problem into 1-D dp, so dp[i] += dp[i - 1], scan from left to right
        // for init dp[0] = 1
        int[] dp = new int[n + 1];
        dp[0] = 1;
        for (int i = 0; i < 5; i++) {
            for (int j = 1; j <= n; j++) {
                dp[j] += dp[j - 1];
            }
        }
        return dp[n];

    }
