    def countVowelStrings_recursive_with_mem(self, n: int) -> int:
        mem = defaultdict(int)

        def count(n, vowels):
            if n == 1:
                return vowels

            if vowels == 1:
                return 1

            if (n, vowels) in mem:
                return mem[(n, vowels)]

            mem[(n, vowels)] = count(n, vowels - 1) + count(n - 1, vowels)
            return mem[(n, vowels)]

        return count(n, 5)

