    int countVowelStrings(int n) {
        vector<int>v{1,1,1,1,1};// declare a vector with all five elements to 1
        // change vector v after every iteration 
        //first time {1,1,1,1,1}--5
        // next time {5,4,3,2,1}--15
        // next time {15,10,6,3,1}--35
