            v1[2]=v1[1]-v[1];
            v1[3]=v1[2]-v[2];
            v1[4]=v1[3]-v[3];
            v=v1;
            dp[i]=v1[0]+v1[1]+v1[2]+v1[3]+v1[4];
        }
       return dp[n-1];
