            sum += abs(m[i][j]);
            zeros += m[i][j] == 0;
            negs += m[i][j] < 0;
            min_abs = min(min_abs, (long long)abs(m[i][j]));
        }
    return sum - (zeros || negs % 2 == 0 ? 0 : 2 * min_abs);
}
