class Solution {
    public long maxMatrixSum(int[][] matrix) {
        int r = matrix.length, c = matrix[0].length;
		long ans = 0;
        boolean alwaystakeAbs = true; // means even number of -ve elements
        int min = Integer.MAX_VALUE;

        outer: for (int i = 0; i < r; i++) {
        	for (int j = 0; j < c; j++) {
        		if (matrix[i][j] == 0) {
        			alwaystakeAbs = true;
        			break outer;
        		}
        		if (matrix[i][j] < 0) {
        			alwaystakeAbs = !alwaystakeAbs;
        		}
        		min = Math.min(min, Math.abs(matrix[i][j]));
        	}
        }

        for (int i = 0; i < r; i++) {
        	for (int j = 0; j < c; j++) {
        		if (alwaystakeAbs) {
        			ans += Math.abs(matrix[i][j]);
        		}
        		else {
        			if (Math.abs(matrix[i][j]) == min) {
        				alwaystakeAbs = true;
        				ans -= min;
        			}
        			else {
        				ans += Math.abs(matrix[i][j]);
        			}
        		}
        	}
        }

		return ans;
    }
}
