class Solution {
    public long maxMatrixSum(int[][] matrix) {
        int numNegative = 0;
        int minNumber = Integer.MAX_VALUE;
        long negativeSum = 0;
        long positiveSum = 0;
        for (int row = 0; row < matrix.length; row++) {
            for (int col = 0; col < matrix[0].length; col++) {
                if (matrix[row][col] <= 0) {
                    numNegative += 1;
                    negativeSum += matrix[row][col];      
                } else {
                    positiveSum += matrix[row][col];
                }
                 minNumber = Math.min(minNumber, Math.abs(matrix[row][col]));
            }
        }
        if (numNegative % 2 == 0) {
            return positiveSum + Math.abs(negativeSum);
        }
        return positiveSum - 2 * minNumber + Math.abs(negativeSum);
    }
}

