impl Solution {
    pub fn max_matrix_sum(matrix: Vec<Vec<i32>>) -> i64 {
        let matrix = matrix
            .iter()
            .map(|row| row.iter().map(|&x| x as i64).collect::<Vec<_>>())
            .collect::<Vec<_>>();
        let mut neg = false;
        let mut sum = 0_i64;
        let mut mini = i64::MAX;
        for row in matrix.iter() {
            for &x in row.iter() {
                if x < 0 {
                    neg = !neg;
                }
                let x = x.abs();
                sum += x;
                if x < mini {
                    mini = x;
                }
            }
        }
        if neg {
            sum -= 2 * mini;
        }
        sum
    }
}
