public class Solution {
    public long MaxMatrixSum(int[][] matrix) {
        var set = new List<long>();
        bool sign = true;
        long sum = 0;
        for (var i = 0; i < matrix.Length; i++)  
        {
            for (var j = 0; j < matrix[i].Length; j++) 
