                maxSum += val ;
                if (matrix[i][j] < 0) negativeCount++ ;
                minValue = Math.min(minValue, val) ;
            }

        if (negativeCount % 2 != 0)
            maxSum -= (2 * minValue) ;
        return maxSum ;
    }
}
