class Solution {
public:
    int dx[4] = {1,-1,0,0};
    int dy[4] = {0,0,1,-1};
    int dfs(int r,int c,int n,vector<vector<int>>& mat){
        mat[r][c] = INT_MAX;
        int sz = 1;
        for(int i=0;i<4;i++){
            int x = r+dx[i], y = c+dy[i];
            if(x>=0 and x<n and y>=0 and y<n and mat[x][y]<=0) sz += dfs(x,y,n,mat);
        }
        return sz;
    }
    long long maxMatrixSum(vector<vector<int>>& matrix) {
        int n = matrix.size();
        long long ans = 0;
        int mn = INT_MAX;
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                mn = min(mn,abs(matrix[i][j]));
                ans += abs(matrix[i][j]);
            }
        }
        int cnt = 0;
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                if(matrix[i][j]<0){
                    int sz = dfs(i,j,n,matrix);
                    if(sz%2) cnt++;
                }
            }
        }
        if(cnt%2) ans -= 2*mn;
        return ans;
    }
};

