import scala.math.abs

object Solution {
  def maxMatrixSum(matrix: Array[Array[Int]]): Long = {
    val n = matrix.length

    def indices = Iterator.range(0, n).flatMap(i => Iterator.tabulate(n)(i -> _))

    def get(index: (Int, Int)) = matrix(index._1)(index._2).toLong

    def absoluteElems = indices.map(get).map(abs)

    val absoluteSum = absoluteElems.sum

    indices.count(get(_) < 0) % 2 match {
      case 0 => absoluteSum
      case _ => absoluteSum - 2 * absoluteElems.minOption.getOrElse(0L)
    }
  }
}
```

