class Solution:
    def maxMatrixSum(self, matrix: List[List[int]]) -> int:
        cnt = 0
        su = 0
        mi = float('inf')
        contains_zero = False
        n = len(matrix)
        m = len(matrix[0])
        for i in range(n):
            for j in range(m):
                if matrix[i][j] <= 0:
                    cnt += 1
                if abs(matrix[i][j]) > 0:
                    mi = min(mi, abs(matrix[i][j]))
                else:
                    contains_zero = True
                su += abs(matrix[i][j])
        if cnt % 2 == 0 or contains_zero:
            return su
        return su + 2 * -abs(mi)
