// 3 Observations:
// - Matrix with zeroes can flush out any number of negative integers through
//   swaps.
// - Matrix with no zeroes and an odd number of negatives will always have a
//   leftover negative integer, therefore we minimize the negative value.
// - Matrix with no zeroes and an even number of negatives will always be capable
//   of erasing all negative integers.
long long maxMatrixSum(
	vector<vector<int>>& matrix)
{
   long long answer = 0;
   if (matrix.size() && matrix[0].size() && matrix.size() == matrix[0].size()) {
	   int dimension = matrix.size();
	   int zeroCount = 0;
	   int negCount = 0;
	   int minAbs = INT_MAX;
	   for (int y = 0; y < dimension; y++)
	   for (int x = 0; x < dimension; x++) {
		   negCount = (matrix[y][x] < 0) ? negCount + 1 : negCount;
		   zeroCount = (matrix[y][x] == 0) ? zeroCount + 1 : zeroCount;
		   minAbs = (minAbs > abs(matrix[y][x])) ? abs(matrix[y][x]) : minAbs;
		   answer += abs(matrix[y][x]);
	   }
	   answer -= ((zeroCount == 0) && (negCount % 2)) ? 2 * minAbs : 0;
   }
   return answer;
}
```

