	class Solution {
    public long maxMatrixSum(int[][] matrix) 
    {
        long sum = 0, min = Integer.MAX_VALUE;
        PriorityQueue<Integer> pq = new PriorityQueue<>();
        for(int i = 0; i < matrix.length; i++)
        {
            for(int j = 0; j < matrix[0].length; j++)
            {
                if(matrix[i][j] >= 0)
                {
                    min = Math.min(min, matrix[i][j]);
                    sum += matrix[i][j];
                }
                else if(matrix[i][j] < 0)
                {
                    pq.add(matrix[i][j]);
                }
            }
        }

        while(pq.size() > 1)
        {
            sum += Math.abs(pq.poll());
            sum += Math.abs(pq.poll());
        }

        if(!pq.isEmpty())
        {
            int last = pq.poll();
            if(min == 0)
            {
                sum += Math.abs(last);
            }
            else if(Math.abs(last) > min)
            {
                sum -= 2*min;
                sum += Math.abs(last);
            }
            else
            {
                sum += last;
            }
        }

        return sum;
    }
	}

