func maxMatrixSum(matrix [][]int) int64 {
	values := make([]int, 0, len(matrix)*len(matrix[0]))
	negative := 0
	for _, row := range matrix {
		for _, item := range row {
			if item < 0 {
				negative++
				values = append(values, -item)
			} else {
				values = append(values, +item)
			}
		}
	}
	sort.Ints(values)
	if negative % 2 == 1 {
		values[0] = -values[0]
	}
	summary := int64(0)
	for _, v := range values {
		summary += int64(v)
	}
	return summary
}
