class Solution {
public:
    long long maxMatrixSum(vector<vector<int>>& matrix) {
        int total_negative=0;
        long long ans=0;
        for(int i=0;i<matrix.size();i++)
        {
            for(int j=0;j<matrix[i].size();j++)
            {
                if(matrix[i][j]<0)
                {
                    total_negative++;
                }
            }
        }
        if(total_negative%2==0)
        {
            long long ans1=0;
            for(int i=0;i<matrix.size();i++)
            {
                for(int j=0;j<matrix[i].size();j++)
                {
                    ans1+=abs(matrix[i][j]);
                }
            }
            return ans1;
        }
        else
        {
            int mini=INT_MAX;
            for(int i=0;i<matrix.size();i++)
            {
                for(int j=0;j<matrix[i].size();j++)
                {
                    mini = min(abs(matrix[i][j]),mini);
                }
            }
            bool flag=false;
            for(int i=0;i<matrix.size();i++)
            {
                for(int j=0;j<matrix[i].size();j++)
                {
                    if(abs(matrix[i][j])==mini && flag==false)
                    {
                        flag=true;
                        continue;
                    }
                    else
                    {
                        ans+=abs(matrix[i][j]);
                    }
                }
            }
            ans-=mini;
        }
        return ans;
    }
};
