class Solution {
    public String mostCommonWord(String paragraph, String[] banned) {

      /*
      Runtime: 10 ms, faster than 70.48% of Java online submissions for Most Common Word.
      Memory Usage: 39.4 MB, less than 78.59% of Java online submissions for Most Common Word.
      */

      // store banned words in a HashSet for fast lookup later
      Set<String> bannedWords = new HashSet<String>(Arrays.asList(banned));

      // convert to lowercase
      paragraph = paragraph.toLowerCase();

      // remove punctuation
      String punctuation = "!?',;.";
      for (char c : punctuation.toCharArray()) {
        paragraph = paragraph.replace(c, ' '); // OK to increase the number of spaces, because our tokenizing will ignore sequences of whitespace
      }

      // tokenize and load non-banned words into HashMap
      String[] tokens = paragraph.split("\\s+"); // split on any amount of whitespace
      Map<String, Integer> frequencyTable = new HashMap<>();      
      for (String s : tokens) {
        if (!bannedWords.contains(s)) {
          frequencyTable.merge(s, 1, Integer::sum); // handy merge() method!
        }
      }

      // traverse HashMap and determine highest-frequency word
      int maxFrequency = 0;
      String maxWord = "";

      for (Map.Entry<String, Integer> e : frequencyTable.entrySet()) {
        if (e.getValue() > maxFrequency) {
          maxFrequency = e.getValue();
          maxWord = e.getKey();
        }
      }

      return maxWord;

    }
}
