        foreach(var word in words) {
            if (bannedWords.Contains(word)) continue;
            if (wordCount.ContainsKey(word)) {
                wordCount[word] = wordCount[word] + 1;                
            } else {
                wordCount.Add(word, 1);
            }

            if (maxWordCount <= wordCount[word]){
                maxWordCount = wordCount[word];
                maxWord = word;
            }
        }

        return maxWord;
    }

