class Solution:
    def getSplit(self, s):
        result = []
        strS = ''
        for i in s.lower():
            if i not in "!?',;. ": strS += i
            else:
                if len(strS) > 0: result.append(strS)
                strS = ''
        if len(strS) > 0: result.append(strS)
        return result

    def mostCommonWord(self, paragraph: str, banned: List[str]) -> str:
        paragraph = self.getSplit(paragraph)
        freq = {}
        for s in paragraph:
            if s not in banned:
                if s in freq: freq[s] += 1
                else: freq[s] = 1

        m = max(freq.values())
        for k in freq:
            if freq[k] == m: return k
