```
class Solution {
public:
    string mostCommonWord(string paragraph, vector<string>& banned) {
        int size = paragraph.size();
        // base case just to check if its null
        if(size == 0) return "";
        // creating map to store 'count' of each word and store banned words in a separate map, so that once we hit a word, we can check if its a banned word in o(1) time
        unordered_map<string,int> count, banned_dict;
        // store the banned word here
        for(auto x:banned){
            banned_dict[x] = 1;
        }
        // intialize the string a empty, and this is the string which will store words in the paragraph
        string curr = "";
        //creating the variable to store max count of any word, and corresponding word, so that at the end we dont have to traverse the count map again to get 'word' with max count (just to reduct extra time).
        int max_count = 0;
        string max_appeared = "";
        for(int i=0;i<size;i++){
            // will only allow if the 'curr char' is a ' ' or char
            if((paragraph[i] > 64 && paragraph[i] < 91) || (paragraph[i] > 96 && paragraph[i] < 123) || paragraph[i] == 32){
                // if we encounter a char, keep on adding as its part of 'curr' string
                if(paragraph[i] != ' '){
                    // this check will ensure if the char is in upper case, it will convert it into lower case and then add to 'curr' string
                    if(paragraph[i] > 64 && paragraph[i] < 91){
                        int z = paragraph[i]+32;
                        curr += z;
                    }
                    else{
                        curr += paragraph[i];
                    }
                }
                // if you encounter ' ', its end of 'curr' word, make required operations and reset 'curr' to empty
                else if(curr.size()){
                    if(banned_dict[curr] == 0){
                        count[curr]++;
                        if(count[curr] > max_count){
                            max_count = count[curr];
                            max_appeared = curr;
                        }
                    }
                    curr = "";
                }
            }
            // if you encounter special symbol, thats again the end of word and after required operations, reset 'curr'
            else if(curr.size()){
                if(banned_dict[curr] == 0){
                    count[curr]++;
                    if(count[curr] > max_count){
                        max_count = count[curr];
                        max_appeared = curr;
                    }
                }
                curr = "";
