class Solution {
public:
    string mostCommonWord(string paragraph, vector<string>& banned) {
	    // Replace all punctuation with spaces.
        replace(paragraph.begin(), paragraph.end(), ',', ' ');
		// If there is still other punctuation, get rid of it.
        paragraph.erase(std::remove_if(paragraph.begin(), paragraph.end(), [](char A){return ispunct(A);}), paragraph.end());

		// Make entire string lower case.
        transform(paragraph.begin(), paragraph.end(), paragraph.begin(), ::tolower);

		// Converts string with spaces to a vector of strings without the spaces.
		// Can have an arbitrary amount of spaces in between words.
        vector<string> ourWords = {istream_iterator<string>{istringstream() = istringstream(paragraph)},
                                   istream_iterator<string>{}};

        unordered_map<string, int> ourMap;

		// Count the frequency of words.
        for (int i{}; i < ourWords.size(); ++i)
        {
            ourMap[ourWords[i]]++;
        }

		// If a word is banned make its frequency 0
        for (int i{}; i < banned.size(); ++i)
        {
            if (ourMap.count(banned[i]))
            {
                ourMap[banned[i]] = 0;
            }
        }
        // Find the most frequent word.
        string res;
        int maxNum{};
        for (const auto& keyValue : ourMap)
        {
            if (keyValue.second > maxNum)
            {
                maxNum = keyValue.second;
                res = keyValue.first;
            }
        }
        return res;
    }
};

