class Solution {
public:
    // Overall - Runtime Complexity: O(n)  Space Complexity O(n)
    string mostCommonWord(string paragraph, vector<string>& banned) {
        // Construct string to store parsed substring from paragraph
        string s = "";

        // Construct unordered map to store word counts
        unordered_map<string, int> um;

        // Iterate over characters in paragraph
        for(int i = 0; i < paragraph.size(); i++) {
            // Check if delimeter is found or at last character in paragraph
            if(!isalpha(paragraph[i]) || i == paragraph.size() - 1) {
                // Check for edge case of last character in paragraph
                if(i == paragraph.size() - 1 && isalpha(paragraph[i]))
                    s += tolower(paragraph[i]);

                // If current string is not empty update map & reset string
                if(!s.empty()) { 
                    um[s]++;
                    s = "";
                }
            } else {
                // Append lower-case of current character to word string
                s += tolower(paragraph[i]);
            }
        }

        // Iterate over banned words
        for(const auto &it : banned) {
            // Check if banned word exists in map
            auto ret = um.find(it);

            // If banned word exists, delete
            if(ret != um.end())
                um.erase(ret->first);
        }

        // Return key in map which contains max value
        return max_element(um.begin(), um.end(),
                          [](const auto &p1, const auto &p2) {
                              return p1.second < p2.second;
                          })->first;
    }
};
