fun mostCommonWord(s: String, ar: Array<String>) = mutableMapOf<String, Int>().apply {
    s.toLowerCase().replace(Regex("[^a-z ]"), " ").split(" ")
        .filter { !ar.contains(it) && it.isNotBlank() }
        .forEach { this[it] = getOrDefault(it, 0) + 1 }
}.maxBy { it.value }?.key!!
