class Solution {
public:
    int maxNumberOfBalloons(string text) {
        std::map<char, int> m;
        int res = INT_MAX;
        std::string balloon = "balloon";

        for (auto c : text)
            if (c == 'b' || c == 'a' || c == 'l' || c == 'o' || c == 'n' )
                m[c]++;
        for (auto c: balloon) {
            if (c == 'l' || c == 'o') {
                res = ( (res < m[c] / 2) ? res : m[c] / 2);
            } else {
                res = ((res < (m[c])) ? res : m[c]);
            }
        }
        return res;
    }
};
