var maxNumberOfBalloons = function(text) {
    const map = {};
    const balloon = 'balloon';
    let count = 0;
    for(let i = 0; i < text.length; i++) {
        map[text[i]] = map[text[i]] > 0 ? map[text[i]] + 1 : 1;
    }

    while(true) {
        for(let i = 0; i < balloon.length; i++) {
            if(map[balloon[i]] > 0) {
                map[balloon[i]]--;
            } else {
                return count;
            }
        }
        count++;
    }

    return count;
};
