```
 string kthSmallestPath(vector<int>& dest, int k) {
        int n = dest[0]+1 , m = dest[1]+1;
        int dp[n][m];
        dp[n-1][m-1] = 0;

        for(int i=n-2;i>=0;i--) dp[i][m-1] = 1;
        for(int i=m-2;i>=0;i--) dp[n-1][i] = 1;

        for(int i=n-2;i>=0;i--)
        {
            for(int j=m-2;j>=0;j--)
            {
                dp[i][j] = dp[i+1][j] + dp[i][j+1];
            }
        }

        string ans;
        int i = 0, j =0;
        while(i<n && j < m)
        {
             if(i+1 == n && j+1 == m) break;

                if(i+1 == n)
                {
                    ans += 'H';
                    j++;
                }
                else if(j+1 == m)
                {
                    ans += 'V';
                    i++;
                }
                else
                {
                    if(k <= dp[i][j+1])
                    {
                        ans += 'H';
                        j++;
                    }
                    else
                    {
                        ans += 'V';
                        k -= dp[i][j+1];
                        i++;
                    }
                }
        }

        return ans;
    }
