class Solution {
public:
    int ncr[31][31];
    string kthSmallestPath(vector<int>& destination, int k) {

        // calculating the combination values
        for(int n=1;n<31;++n)
        {
            ncr[n][0]=1;
            for(int r=1;r<n;++r)
            {
                ncr[n][r]=ncr[n-1][r]+ncr[n-1][r-1];
            }
            ncr[n][n]=1;

        }
        string res="";
        int H=destination[1];
        int V=destination[0];

        while(H!=0 && V!=0)
        {
            int ways=ncr[H+V-1][H-1];


            if(ways>=k)
            {
                res+='H';
                H--;
            }
            else
            {
                res+='V';
                k-=ways;
                V--;
            }
        }
        while(H--)
            res+='H';
        while(V--)
            res+='V';
        return res;
    }
};
```

