```c++
const int MXN = 15 + 2;

class Solution {
    int n, m;

    int dp[MXN][MXN];

    int go(int x, int y) {
        if(x==n && y==m) return 1;
        if(x < 0 || x > n || y < 0 || y > m) return 0;
        int &ans = dp[x][y];
        if(ans != -1) return ans;
        ans = go(x+1, y) + go(x, y+1);
        return ans;
    }

    string way;
    void build(int x, int y, int k) {
        if(x==n && y==m) return;
        if(x < 0 || x > n || y < 0 || y > m) return;
        if(k <= go(x, y+1)) {
            way.push_back('H');
            build(x, y+1, k);
        } else {
            k -= go(x, y+1);
            way.push_back('V');
            build(x+1, y, k);
        }
    }

public:
    string kthSmallestPath(vector<int>& destination, int k) {
        n = destination[0];
        m = destination[1];
        memset(dp, -1LL, sizeof(dp));
        go(0, 0);
        build(0, 0, k);
        return way;
    }
};
```

