Step by step fill the string ans

* Count the number of permutations that exist if H is placed at index i of string ans
* If the kth permutation lies in this set of permutations, then place H and continue for the next i
* Else place V and move the search window to the next set, and continue for the next i

```
#include<bits/stdc++.h>
typedef unsigned long long int ll;
class Solution {
public:
    ll ncr(int n, int r)
    {
        ll ans = 1;
        int r1 = max(r, n-r);
        int r2 = n-r1;

        for(int i = 1; i<=r2; i++)
        {
            ans*=ll(n);
            n--;
            ans/=ll(i);
        }

        return ans;
    }

    string kthSmallestPath(vector<int>& ds, int k) {
        ios_base::sync_with_stdio(false);
        cin.tie(NULL);
        string ans = "";
        int x = ds[1];
        int y = ds[0];
        int n = (x+y);

        ll np = ncr(n, y);

        ll l = 1;
        ll r = np;

        for(int i = 0; i<n; i++)
        {
            if(x==0)
            {
                ans+="V";
                y--;
                continue;
            }
            if(y==0)
            {
                ans+="H";
                x--;
                continue;
            }

            ll nph = ncr(x-1+y, y);
            ll nr = l+nph-ll(1);
            if(nr >= ll(k))
            {
                ans+="H";
                x--;
            }
            else
            {
                ans+="V";
                y--;
                l = nr+ll(1);
            }

        }

        return ans;
    }
};
```

