class Solution {
    public String kthSmallestPath(int[] destination, int k) {
        StringBuilder sb = new StringBuilder();
        int n = destination[0] + destination[1];
        for (int i = 0; i < n; i++) {
            long cnt = comb(n - i - 1, destination[0]); //look at the subsequent possible characters after this one. n - i - 1.
            if (destination[0] == 0) {
                sb.append('H');
                destination[1]--;
                continue;
            }
            if (destination[1] == 0) {
                sb.append('V');
                destination[0]--;
                continue;
            }
            if (k <= cnt) {
                destination[1]--;
                sb.append('H');
            }
            else {
                destination[0]--;
                k -= cnt;
                sb.append('V');
            }
        }
        return sb.toString();
    }

    private long comb(int n, int k) {
        long res = 1;
        for (int i = 0; i < k; i++) {
            res *= n - i; // getting rid of n - k and onwards.
            res /= i + 1; // including division of k!.
        }
        return res;
    }
}
