func kthSmallestPath(destination []int, k int) string {
	m, n := destination[0] + 1, destination[1] + 1
	dp := make([][]int, m)
	for i := range dp { dp[i] = make([]int, n) }
	for i := 0; i < n - 1; i++ { dp[m-1][i] = 1 }
	for i := 0; i < m - 1; i++ { dp[i][n-1] = 1 }
	for i := m - 2; i >= 0; i-- { for j := n - 2; j >= 0; j-- { dp[i][j] = dp[i][j+1] + dp[i+1][j] } }
	var ret string
	i, j := 0, 0
	for i < m - 1 || j < n - 1 {
		if i == m - 1 {
			ret += "H"
			j++
		} else if j == n - 1 {
			ret += "V"
			i++
		} else if k <= dp[i][j+1] {
			ret += "H"
			j++
		} else {
			ret += "V"
            k -= dp[i][j+1]
			i++
		}
	}
	return ret
}
