    unordered_map<string, long long> nck_cache; 
    long long factorial(long long x) {
        if (fact_cache.count(x)) return fact_cache[x];
        long long res = 1;

        if (x > 0) res = (long long) x * factorial(x-1);

        fact_cache[x] = res;
        return res;
    }

    int nCk(int n, int k) {
        // long long den = factorial(k) * factorial(n-k);
        // long long num = factorial(n);

        string key = to_string(n) + "C" + to_string(k);
        if (nck_cache.count(key)) return nck_cache[key];
        long long den = factorial(min(k,n-k));
        long long num = 1;
        for (long long nn = n; nn > (long long) max(n-k,k); --nn) {
            num *= nn;
        }
        int res = (int) (num/den);
        nck_cache[key] = res;


        return res;
    }

    string kthSmallestPath(vector<int>& destination, int k) {
        int V = destination[0], H = destination[1];
        int n = H + V;
        vector<char> order({'H', 'V'});
        string path = "";

        for (int i = 0; i < n; ++i) {
            if (!H) {
                --V;
                path.push_back('V');
            }
            else if (!V) {
                --H;
                path.push_back('H');
            }
            else {
                // if 0 placed in place i
                int num0 = nCk(V+H-1, max(V,H-1));
                // cout << k << " " << H << " " << V << " " << n-i-1 << "C" << max(H-1,V) << " " << num0 << endl;

                if (num0+1 > k) {
                    --H;
                    path.push_back(order[0]);
                }
                else {
                    k -= num0;
                    --V;
                    path.push_back(order[1]);
                }
            }
        }

        return path;
    }
};
```

