	//This funciton is used to find the ncr which mentioned in the question.
    private int ncr(int a, int b){
        int count = 1;
        int prod = 1;
        while (count <= b) {
            prod = prod * a / count;
            a--;
            count++;
        }
        return prod;
    }
}
