First pass: calculate count of all path from (row, col) to destination
Second pass: fill k-th trace using first pass calculations

```
    public class Solution
    {
        private static readonly int[][] _dirs = new[]
        {
            new[] {0, 1},
            new[] {1, 0},
        };

        private int CalculateCount(int?[,] count, int n, int m, int row, int col)
        {
            if (count[row, col].HasValue)
            {
                return count[row, col].Value;
            }

            int res = 0;

            foreach (var d in _dirs)
            {
                int newRow = row + d[0];
                int newCol = col + d[1];

                if (newRow < n && newCol < m)
                {
                    res += CalculateCount(count, n, m, newRow, newCol);
                }
            }

            count[row, col] = res;
            return res;
        }

        private void Helper(StringBuilder res, int row, int col, int?[,] counts, int k)
        {
            int n = counts.GetLength(0);
            int m = counts.GetLength(1);

            if (row == n - 1 && col == m - 1)
            {
                return;
            }

            var horRow = row + _dirs[0][0];
            var horCol = col + _dirs[0][1];

            var vertRow = row + _dirs[1][0];
            var vertCol = col + _dirs[1][1];

            if (horRow < n && horCol < m)
            {
                if (k < counts[horRow, horCol].Value)
                {
                    res.Append('H');
                    Helper(res, horRow, horCol, counts, k);
                }
                else
                {
                    res.Append('V');
                    Helper(res, vertRow, vertCol, counts, k - counts[horRow, horCol].Value);
                }
            }
            else
            {
                res.Append('V');
                Helper(res, vertRow, vertCol, counts, k);
            }
        }

        public string KthSmallestPath(int[] destination, int k)
        {
            int n = destination[0] + 1;
            int m = destination[1] + 1;

            int?[,] count = new int?[n,m];
            count[destination[0], destination[1]] = 1;
            CalculateCount(count, n, m, 0, 0);
            StringBuilder res = new StringBuilder();
            Helper(res, 0,0,count, k-1);
            return res.ToString();
        }
    }
