```
public class Solution {
    int[,] nCr;
    public string KthSmallestPath(int[] destination, int k) {
        StringBuilder res=new StringBuilder();
        int n=destination[0];int m=destination[1];
        nCr=new int[n+m+1,n+1];

        int distance=destination[0]+destination[1];
        int[] curr_loc=new int[2]{0,0};

        for(int i=0;i<distance;i++){
            long c=C(distance-i-1,destination[0]-curr_loc[0]) ;   
            if(k>c){
                res.Append("V");
                curr_loc[0]++;
                k=k-(int)c;
            }
            else{
                res.Append("H");
                curr_loc[1]++;
            }

        }

        return res.ToString();
    }

    public int C(int n, int r){
        if(r==0 || r==n)
            return 1;

        if(r>n)
            return 0;
        if(nCr[n,r]>0)
            return nCr[n,r];

        nCr[n,r]= C(n-1,r)+C(n-1,r-1);
        return nCr[n,r];
    }
}
