    public int C(int n, int r){
        if(r==0 || r==n)
            return 1;

        if(r>n)
            return 0;
        if(nCr[n,r]>0)
            return nCr[n,r];

        nCr[n,r]= C(n-1,r)+C(n-1,r-1);
        return nCr[n,r];
    }
}
