                int c = comb(x + y - 1, x - 1);
                if (k <= c) {
                    ans.append('H');
                    x--;
                } else {
                    ans.append('V');
                    y--;
                    k -= c;
                }
            } else {
                ans.append('V');
                y--;
            }
        }

        return ans.toString();
    }
}

