public class Solution {
    public string KthSmallestPath(int[] destination, int k)
    {
        int vCount = destination[0];
        int hCount = destination[1];
        int totalCount = vCount + hCount;
        int indexCountNow = 0;
        int remainingVCount = vCount;
        StringBuilder sb = new StringBuilder();

        for (int i = 0; i < totalCount; ++i)
        {
            if (remainingVCount <= 0)
            {
                int remainingHCount = totalCount - i;
                for (int whichH = 0; whichH < remainingHCount; ++whichH)
                {
                    sb.Append('H');
                }
                break;
            }

            int remainingN = totalCount - 1 - i;
            if (remainingN < remainingVCount)
            {
                for (int whilchV = 0; whilchV < remainingVCount; ++whilchV)
                {
                    sb.Append('V');
                }
                break;
            }

            int partitionCount = CombinationCount(remainingN, remainingVCount);
            indexCountNow += partitionCount;

            if (k <= indexCountNow)
            {
                sb.Append('H');
                indexCountNow -= partitionCount;
            }
            else
            {
                sb.Append('V');
                --remainingVCount;
            }
        }

        return sb.ToString();
    }

    private int CombinationCount(int a, int b)
    {
        long answer = 1;

        for (int i = 1; i <= b; ++i)
        {
            answer *= (a - b + i);
            answer /= i;
        }

        return (int)answer;
    }
}
```

