object Solution {
    def kthSmallestPath(des: Array[Int], k: Int): String = {
        def mem[I, O](f: I => O) = new scala.collection.mutable.HashMap[I, O]() {
            override def apply(key: I) = getOrElseUpdate(key, f(key))
        }

        lazy val comb: ((Int, Int)) => Int = mem {
            case (_, 0) | (0, _) => 1
            case (m, 1) => m
            case (m, n) if (n > m / 2) => comb(m, m - n)
            case (m, n) => comb(m - 1, n - 1) + comb(m - 1, n)
        }

        lazy val go: ((Int, Int, Int)) => String = {
            case (r, 0, _) => "V" * r
            case (r, c, k) if (k > comb(r + c - 1, r)) => "V" + go(r - 1, c, k - comb(r + c - 1, r))
            case (r, c, k) => "H" + go(r, c - 1, k)
        }

        go(des(0), des(1), k)
    }
}
