import math 
class Solution:

    def kthSmallestPath(self, destination: List[int], k: int) -> str:
        v, h = destination
        ret = []
        while True:
            if v == 0:
                ret.append("H" * h)
                break
            if h == 0:
                ret.append("V" * v)
                break
            # the number of versions that start with an H
            threshold = math.comb(h + v - 1, h - 1)
            if k <= threshold:
                ret.append("H")
                h -= 1
            else:
                ret.append("V")
                v -= 1
                k -= threshold
        return "".join(ret)
