class Solution {

    int combi(int n, int r) {

        int num = n;
        int den = min(n-r, r);

        int sm = 1;
        int i;

        for(i=1;i<=den;i++) {
            sm = (sm * num)/i;
            num--;
        }        
        return sm;
    }

    string solve(int h, int v, int k) {
        if(h==0) {
            return string(v,'V');
        }
        if(v==0) {
            return string(h, 'H');
        }

