func kthSmallestPath(destination []int, k int) string {
	dx, dy := destination[0], destination[1]

	m := make([][]int, dx+1)
	for i := 0; i < dx+1; i++ {
		m[i] = make([]int, dy+1)
	}

	for i := 0; i <= dx; i++ {
		for j := 0; j <= dy; j++ {
			if i == 0 && j == 0 {
				m[i][j] = 1
			} else if i == 0 {
				m[i][j] = m[i][j-1]
			} else if j == 0 {
				m[i][j] = m[i-1][j]
			} else {
				m[i][j] = m[i][j-1] + m[i-1][j]
			}
		}
	}

	result := ""

	for dx > 0 && dy > 0 {
 		if m[dx][dy-1] >= k {
			dy--
			result += "H"
		} else {
			k -= m[dx][dy-1]
			dx--
			result += "V"
		}
	}

	for dx > 0 {
		dx--
		result += "V"
	}
	for dy > 0 {
		dy--
		result += "H"
	}

	return result
}
