class Solution:
    def kthSmallestPath(self, destination: List[int], k: int) -> str:
        from math import comb
        row, col = destination
        currow, curcol = 0, 0
        res = []
        while curcol < col and currow < row:
            cnt = comb(row-currow+col-curcol-1, row-currow)
            if cnt >= k:
                curcol += 1
                res.append("H")
            else:
                currow += 1
                k -= cnt
                res.append("V")
        res.extend(["H"]*(col-curcol))
        res.extend(["V"]*(row-currow))
        return "".join(res)
