class Solution(object):
    def kthSmallestPath(self, destination, k):
        """
        :type destination: List[int]
        :type k: int
        :rtype: str
        """
        def C(A,N):
            if A <= N: return 1
            return factorial(A)/factorial(A-N)/factorial(N)  

        def Assemble(H,V,K):
            if H == 0 or V == 0: return 'H' * H + 'V' * V
            total = C(H+V,V)
            if total <= K: return 'V' * V + 'H' * H
            else:
                lower = C(H+V-1,V)
                if lower >= K:
                    return 'H' + Assemble(H-1,V, K)
                else:
                    return 'V' + Assemble(H, V-1, K-lower)

        return Assemble(destination[1], destination[0], k)
